DESC Salon_Visits;

SELECT COUNT(*) AS TotalVisits
FROM Salon_Visits;

SELECT
    CustomerID,
    COUNT(*) AS TotalVisits
FROM Salon_Visits
GROUP BY CustomerID
ORDER BY TotalVisits DESC;

SELECT
    CASE
        WHEN visit_count = 1 THEN 'New'
        ELSE 'Repeat'
    END AS CustomerType,
    COUNT(*) AS TotalCustomers
FROM (
    SELECT CustomerID, COUNT(*) AS visit_count
    FROM Salon_Visits
    GROUP BY CustomerID
) t
GROUP BY CustomerType;

SELECT
    ServiceCategory,
    COUNT(*) AS TotalVisits
FROM Salon_Visits
GROUP BY ServiceCategory
ORDER BY TotalVisits DESC;

SELECT
    ServiceCategory,
    CASE
        WHEN visit_count = 1 THEN 'New'
        ELSE 'Repeat'
    END AS CustomerType,
    COUNT(*) AS TotalCustomers
FROM (
    SELECT CustomerID, ServiceCategory, COUNT(*) AS visit_count
    FROM Salon_Visits
    GROUP BY CustomerID, ServiceCategory
) t
GROUP BY ServiceCategory, CustomerType
ORDER BY ServiceCategory;

SELECT
    CustomerID,
    COUNT(DISTINCT ServiceCategory) AS ServiceCount
FROM Salon_Visits
GROUP BY CustomerID
HAVING COUNT(DISTINCT ServiceCategory) > 1;


SELECT SUM(Amount) AS Total_Revenue
FROM salon_visits;
